## sample

```c++
#include <glog/logging.h>
#include <yaml-cpp/yaml.h>

#include <eigen3/Eigen/Eigen>
#include <fstream>
#include <iostream>
#include <vector>

void GetPgCamParm(const std::string camera_param, const std::string cali_param,
                  const std::string result_param) {
  YAML::Node cam_info = YAML::LoadFile(camera_param);
  YAML::Node cb_xyz = YAML::LoadFile(cali_param);

  // cam_k
  std::vector<double> v_cam_k;
  if (cam_info["mtx"]["data"]) {
    v_cam_k = cam_info["mtx"]["data"].as<std::vector<double>>();
  } else {
    LOG(FATAL) << "Can't get cam_info[\"mtx\"][\"data\"]";
  }
  auto cam_k =
      Eigen::Map<Eigen::Matrix<double, 3, 3, Eigen::RowMajor>>(v_cam_k.data());
  VLOG(2) << "\ncam_k:\n" << cam_k;

  // cam_intra
  auto col = Eigen::Vector3d(0, 0, 0);
  Eigen::MatrixXd tmp(3, 4);
  tmp << cam_k, col;
  auto row = Eigen::RowVector4d(0, 0, 0, 1);
  Eigen::Matrix<double, 4, 4, Eigen::RowMajor> cam_intra;
  cam_intra << tmp, row;
  VLOG(2) << "\ncam_intra:\n" << cam_intra;

  // R_e2cab
  Eigen::Matrix3d R_e2cab;
  R_e2cab << 0, 0, -1,  //
      0, 1, 0,          //
      1, 0, 0;          //
  VLOG(2) << "\nR_e2cab:\n" << R_e2cab;

  // T_e2cab
  Eigen::Vector3d T_e2cab;
  if (cb_xyz["boarad2ego"]) {
    T_e2cab << cb_xyz["boarad2ego"]['x'].as<double>(),
        cb_xyz["boarad2ego"]['y'].as<double>(),
        cb_xyz["boarad2ego"]['z'].as<double>();
  } else {
    LOG(FATAL) << "Can't get cb_xyz[\"boarad2ego\"]";
  }
  VLOG(2) << "\nT_e2cab:\n" << T_e2cab;

  // ego2cab
  Eigen::MatrixXd ego2cab_tmp(3, 4);
  ego2cab_tmp << R_e2cab, T_e2cab;
  VLOG(2) << "\nego2cab_tmp:\n" << ego2cab_tmp;

  // R_cab2cam
  std::vector<double> v_R_cab2cam;
  if (cam_info["rvec"]["data"]) {
    v_R_cab2cam = cam_info["rvec"]["data"].as<std::vector<double>>();
  } else {
    LOG(FATAL) << "Can't get cam_info[\"rvec\"][\"data\"]";
  }
  auto R_cab2cam_tmp = Eigen::Map<Eigen::RowVector3d>(v_R_cab2cam.data());
  double n_norm = R_cab2cam_tmp.norm();
  Eigen::AngleAxisd rotation_vector(n_norm, R_cab2cam_tmp / n_norm);
  Eigen::Matrix3d R_cab2cam = rotation_vector.toRotationMatrix();
  VLOG(2) << "\nR_cab2cam:\n" << R_cab2cam;

  // T_cab2cam
  std::vector<double> v_T_cab2cam;
  if (cam_info["tvec"]["data"]) {
    v_T_cab2cam = cam_info["tvec"]["data"].as<std::vector<double>>();
  } else {
    LOG(FATAL) << "Can't get cam_info[\"tvec\"][\"data\"]";
  }
  auto T_cab2cam = Eigen::Map<Eigen::Vector3d>(v_T_cab2cam.data());
  VLOG(2) << "\nT_cab2cam:\n" << T_cab2cam;

  // cab2cam
  Eigen::MatrixXd cab2cam_tmp(3, 4);
  cab2cam_tmp << R_cab2cam, T_cab2cam;
  auto col_tmp = Eigen::RowVector4d(0, 0, 0, 1);
  Eigen::MatrixXd cab2cam(4, 4);
  cab2cam << cab2cam_tmp, col_tmp;
  VLOG(2) << "\ncab2cam:\n" << cab2cam;

  // ego2cab
  Eigen::MatrixXd ego2cab(4, 4);
  ego2cab << ego2cab_tmp, col_tmp;
  VLOG(2) << "\nego2cab:\n" << ego2cab;

  // cam2ego
  Eigen::MatrixXd cam2ego = (cab2cam * ego2cab).inverse();
  VLOG(2) << "\ncam2ego:\n" << cam2ego;
  LOG(INFO) << "\ncam2ego row:\n" << cam2ego.row(0);
  LOG(INFO) << "\ncam2ego row:\n" << cam2ego.col(0);

  // get results
  LOG(INFO) << "\nintrinsic: \n" << cam_intra;
  std::vector<double> vec_cam_intra(
      cam_intra.data(), cam_intra.data() + cam_intra.rows() * cam_intra.cols());

  Eigen::Matrix<double, 3, 3, Eigen::RowMajor> R_cam2ego =
      cam2ego.block(0, 0, 3, 3);
  LOG(INFO) << "\nR_cam2ego: \n" << R_cam2ego;
  std::vector<double> vec_R_cam2ego(
      R_cam2ego.data(), R_cam2ego.data() + R_cam2ego.rows() * R_cam2ego.cols());

  Eigen::Vector3d T_cam2ego = cam2ego.block(0, 3, 3, 1);
  LOG(INFO) << "\nT_cam2ego: \n" << T_cam2ego;
  std::vector<double> vec_T_cam2ego(
      T_cam2ego.data(), T_cam2ego.data() + T_cam2ego.rows() * T_cam2ego.cols());

  // save result to yaml
  YAML::Node config;
  // test["camera"]["v_cam_k"].push_back(v);
  // note: push_back and = are not same
  config["camera"]["intrinsic"] = vec_cam_intra;
  config["camera"]["cam2ego_R"] = vec_R_cam2ego;
  config["camera"]["cam2ego_t"] = vec_T_cam2ego;
  std::ofstream fout(result_param);
  fout << config;
}

int main(int argc, char *argv[]) {
  GetPgCamParm("camera_params.yaml", "cali_board2vehicle_xyz.yaml",
               "pg_cam2_param.yaml");
  return 0;
}

```

