1. [OpenCV 一个窗口同时显示多张图片](https://python.iitter.com/other/302393.html)

```
static cv::Mat concat_images(std::vector<cv::Mat> imgs) {
  using namespace cv;
  const int MAX_PIXEL = 540;
  int imgs_num = imgs.size();
  auto sqrt_value = std::sqrt(imgs_num);
  Size first_img_size = imgs[0].size();
  int max_pixel = std::max(first_img_size.height, first_img_size.width);
  double prop = max_pixel < MAX_PIXEL ? (double)max_pixel / (double)MAX_PIXEL
                                      : (double)MAX_PIXEL / (double)max_pixel;
  Size std_size(first_img_size.width * prop, first_img_size.height * prop);
  VLOG(1) << "std size: " << std_size;

  int img_cols = (sqrt_value - (int)sqrt_value == 0.0)
                     ? std::floor(sqrt_value)
                     : std::floor(sqrt_value) + 1;
  VLOG(1) << "img_cols:  " << img_cols;
  Mat tmp_img;
  Point2i location(0, 0);
  Mat result(std_size.height * ((imgs_num - 1) / img_cols + 1),
             std_size.width * img_cols, imgs[0].type());
  VLOG(1) << "concat image size: " << result.size();
  for (int i = 0; i < imgs_num; i++) {
    location.x = (i % img_cols) * std_size.width;
    location.y = (i / img_cols) * std_size.height;
    resize(imgs[i], tmp_img, std_size, prop, prop, INTER_LINEAR);
    tmp_img.copyTo(result(Rect(location, std_size)));
  }
  return result;
}

